mapper.select:
SELECT * FROM ( SELECT /*+ INDEX_DESC(vam_author SYS_C0010457) */ ROWNUM rn,bookId,bookName, (SELECT authorName FROM vam_author WHERE vam_book.authorId = vam_author.authorId) authorName, (SELECT cateName FROM vam_bcate WHERE vam_book.cateCode = vam_bcate.cateCode) cateName, bookStock,regDate FROM vam_book WHERE
bookName like '%'||#{keyword}||'%' AND
ROWNUM <= #{pageNum} * #{amount} ) WHERE rn > (#{pageNum}-1) * #{amount}

mapper.update:
  		UPDATE vam_book SET bookName = #{bookName}, authorId = #{authorId}, publeYear = #{publeYear}, publisher = #{publisher}, cateCode = ${cateCode}, 
			bookPrice = #{bookPrice}, bookStock = #{bookStock}, bookDiscount = #{bookDiscount}, bookIntro = #{bookIntro}, bookContents = #{bookContents}, updateDate = sysdate
			where bookId = ${bookId}
